mapper.select:
SELECT * FROM sys_dict WHERE del_flag = #{DEL_FLAG_NORMAL}
AND type = #{type}
AND description LIKE '%'||#{description}||'%' CONCAT('%', #{description}, '%')
ORDER BY type, sort, update_date DESC

update.foreach:
UPDATE sys_dict SET 
	label = #{item.label}, 
	update_date = #{item.updateDate}
WHERE type = #{item.type} and value= #{item.value}

mapper.update:
UPDATE sys_dict SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
