mapper.select:
SELECT  

FROM material_type 

order by code ${sortDir}
limit ${start},${limit}

SELECT
FROM material_type
order by CREATE_DATE DESC limit ${start},${limit}

mapper.update:
update material_type
set TYPE_COUNT = TYPE_COUNT + #{increment}
where CODE = #{code,jdbcType=VARCHAR}

update material_type
set TYPE_COUNT = TYPE_COUNT - #{decrement}
where CODE = #{code,jdbcType=VARCHAR}
